import os
from kodipopcorntime import plugin

@plugin.route("/cmd/install")
def firstrun():
    clear_cache()
    plugin.notify(plugin.addon.getLocalizedString(30300))
    plugin.open_settings()


@plugin.route("/cmd/clear_cache")
def clear_cache():
    import os
    import glob
    from kodipopcorntime.caching import CACHE_DIR
    for directory in [CACHE_DIR, plugin.storage_path]:
        for dbfile in glob.glob(os.path.join(directory, "*.db")):
            os.remove(dbfile)
    for file in glob.glob(CACHE_DIR + '/com.imdb.*'):
        os.remove(file)
    for file in glob.glob(CACHE_DIR + '/kodipopcorntime.route.*'):
        os.remove(file)
    plugin.notify(plugin.addon.getLocalizedString(30301))


firstrun_file = os.path.join(plugin.addon.getAddonInfo("path"), ".firstrun")
if not os.path.exists(firstrun_file):
    with open(firstrun_file, "w"):
        firstrun()
